int howManyCollisions(string s){
    int ans = (int) s.size();
    for(int i = 0; i < (int) s.size(); i++){
        if(s[i] != 'l')
            break;
        ans--;
    }
    for(int i = (int) s.size() - 1; i >= 0; i--){
        if(s[i] != 'r')
            break;
        ans--;
    }
    for(int i = 0; i < (int) s.size(); i++)
        if(s[i] == 'd')
            ans--;
    return ans;
}

