int lef = Maxn, rig;
ll cur;
ll res[Maxn];

void Add(int ind)
{
    if (lef == Maxn) {
        cur++;
        lef = rig = ind;
    } else if (ind + 1 == lef) {
        lef--;
        cur += min(rig + 1, R[ind]) - ind;
    } else {
        rig++;
        cur += ind - max(lef - 1, L[ind]);
    }
}

void Rem(int ind)
{
    if (lef == rig) { cur--; lef = Maxn; }
    else if (lef == ind) {
        lef++;
        cur -= (min(rig + 1, R[ind]) - ind);
    } else {
        rig--;
        cur -= (ind - max(lef - 1, L[ind]));
    }
}

